SELECT FP.CATEGORY, FP.PRICE AS MAX_PRICE, FP.PRODUCT_NAME FROM FOOD_PRODUCT AS FP JOIN (SELECT CATEGORY,MAX(PRICE) AS MAX_PRICE FROM FOOD_PRODUCT WHERE CATEGORY = '식용유' or CATEGORY = '과자' or CATEGORY = '국' or CATEGORY = '김치' GROUP BY CATEGORY) AS SUB ON FP.CATEGORY = SUB.CATEGORY AND FP.PRICE = SUB.MAX_PRICE ORDER BY MAX_PRICE DESC